    // Método para criar um SVGPath com o conteúdo, cor e tamanho fornecidos
    private static SVGPath createSVG(String content, double size, Color color) {
        SVGPath svg = new SVGPath();
        svg.setContent(content);
        svg.setFill(color);

        // Redimensiona o ícone com base no tamanho fornecido
        double scale = size / 100; // 100 é o tamanho base dos ícones
        svg.setScaleX(scale);
        svg.setScaleY(scale);

        return svg;
    }

